mapper.select:
SELECT			ROUT_ID, ROUT_NM
FROM			BMS_ROUT_MST
WHERE			DEL_YN = 'N'	
AND 			WAY_DIV = #{WAY_DIV}

	AND			ROUT_GRP = #{CONTENT1}

SELECT COUNT(*) CNT FROM BMS_OPER_ALLOC_PL_NODE_INFO A LEFT JOIN BMS_NODE_MST B ON A.NODE_ID = B.NODE_ID WHERE A.ALLOC_ID = #{ALLOC_ID}
AND C.ROUT_ID = #{ROUT_ID}
AND (B.NODE_TYPE = 'NT001' OR B.NODE_TYPE = 'NT002')

mapper.update:
UPDATE BMS_OPER_ALLOC_PL_NODE_INFO
SET ARRV_TM = #{ARRV_TM}, DPRT_TM = #{DPRT_TM}, UPD_DTM = #{UPD_DTM}, UPD_ID = #{SSN_USER_ID}

WHERE  ALLOC_ID = #{ALLOC_ID} AND SN = #{SN} AND NODE_ID = #{NODE_ID} AND NODE_SN = #{NODE_SN} 
AND		ALLOC_NO = #{ALLOC_NO}

mapper.select:
SELECT A.ALLOC_ID ,A.ROUT_ID ,A.WAY_DIV ,WD.DL_CD_NM AS WAY_DIV_NM ,A.OPER_SN ,A.NODE_ID ,A.NODE_SN ,A.ALLOC_NO ,A.COR_ID ,D.NODE_TYPE ,NT.DL_CD_NM AS NODE_TYPE_NM ,A.ARRV_TM ,A.DPRT_TM ,DATE_FORMAT(A.UPD_DTM, '%Y-%m-%d') UPD_DTM ,A.UPD_ID ,D.NODE_NM ,C.ACCRU_LEN FROM BMS_OPER_ALLOC_PL_NODE_INFO A LEFT JOIN BMS_ROUT_MST RM ON A.ROUT_ID= RM.ROUT_ID LEFT JOIN BMS_DL_CD_INFO WD ON A.WAY_DIV= WD.DL_CD AND WD.CO_CD = 'WAY_DIV' INNER JOIN BMS_MAIN_ROUT_NODE_INFO B ON A.ROUT_ID = B.ROUT_ID AND A.NODE_ID = B.NODE_ID AND A.NODE_SN = B.NODE_SN INNER JOIN BMS_ROUT_NODE_CMPSTN C ON B.ROUT_ID = C.ROUT_ID AND B.NODE_ID = C.NODE_ID AND B.NODE_SN = C.NODE_SN LEFT JOIN BMS_NODE_MST D ON A.NODE_ID = D.NODE_ID LEFT JOIN BMS_DL_CD_INFO NT ON D.NODE_TYPE= NT.DL_CD AND NT.CO_CD = 'NODE_TYPE' WHERE D.ALLOC_ID = #{ALLOC_ID} AND (D.NODE_TYPE = 'NT001' OR A.NODE_TYPE = 'NT002')
AND A.OPER_SN = #{OPER_SN}
ORDER BY OPER_SN, NODE_SN
