mapper.select:
SELECT DISTINCT DATE_FORMAT(A.OPER_DT, '%Y-%m-%d') OPER_DT
,A.ALLOC_ID ,A.SN ,A.NODE_ID ,C.NODE_NM ,C.NODE_TYPE ,DD.DL_CD_NM NODE_TYPE_NM ,A.NODE_SN ,A.ALLOC_NO ,A.OPER_SN ,A.ROUT_ID ,B.ROUT_NM ,A.WAY_DIV ,A.COR_ID ,A.ARRV_TM ,A.DPRT_TM ,A.UPD_DTM
FROM BMS_DAY_OPER_ALLOC_PL_NODE_INFO A
LEFT JOIN BMS_ROUT_MST B ON A.ROUT_ID = B.ROUT_ID LEFT JOIN BMS_NODE_MST C ON A.NODE_ID = C.NODE_ID LEFT JOIN BMS_DL_CD_INFO DD ON C.NODE_TYPE = DD.DL_CD AND DD.CO_CD = 'NODE_TYPE'
WHERE A.ALLOC_ID = #{ALLOC_ID} AND A.OPER_DT = #{OPER_DT}
AND A.ROUT_ID = #{ROUT_ID}
AND A.OPER_SN = #{OPER_SN}
ORDER BY ALLOC_ID, ALLOC_NO, OPER_SN, NODE_SN

SELECT COUNT(*) CNT FROM BMS_DAY_OPER_ALLOC_PL_NODE_INFO A LEFT OUTER JOIN BMS_MAIN_ROUT_NODE_INFO B ON A.ROUT_ID = B.ROUT_ID AND A.NODE_ID = B.NODE_ID AND A.NODE_SN = B.NODE_SN AND (B.NODE_TYPE = 'NT001'||B.NODE_TYPE = 'NT002') WHERE A.ALLOC_ID = #{ALLOC_ID} AND A.OPER_DT = #{OPER_DT}
AND A.ROUT_ID = #{ROUT_ID}
AND (A.NODE_TYPE = 'NT001'||A.NODE_TYPE = 'NT002') GROUP BY OPER_SN ORDER BY OPER_SN

mapper.update:
UPDATE BMS_DAY_OPER_ALLOC_PL_NODE_INFO
SET ARRV_TM = #{ARRV_TM}, DPRT_TM = #{DPRT_TM}, UPD_DTM = #{UPD_DTM}
WHERE  ALLOC_ID = #{ALLOC_ID} AND SN = #{SN} AND NODE_ID = #{NODE_ID} AND NODE_SN = #{NODE_SN}
