mapper.delete:
DELETE 
FROM 			BMS_ALLOC_PL_INFO
WHERE 			ALLOC_ID = #{ALLOC_ID}
AND 			SN = #{SN}

mapper.insert:
INSERT INTO BMS_DAY_ALLOC_PL_INFO(
	OPER_DT
	,ALLOC_ID
	,ALLOC_NO
	,WAY_DIV
	,VHC_ID
	,SEC_VHC_ID
	,SEC_TRAN_TM
	,UPD_DTM
)VALUES(
	DATE_FORMAT(#{OPER_DT}, '%Y-%m-%d')
	,#{ALLOC_ID}
	,#{ALLOC_NO}
	,#{WAY_DIV}
	,#{VHC_ID}
	,#{SEC_VHC_ID}
	,#{SEC_TRAN_TM}
	,#{UPD_DTM}
)ON DUPLICATE KEY UPDATE
	VHC_ID = VALUES(VHC_ID)
	,SEC_VHC_ID = VALUES(SEC_VHC_ID)
	,SEC_TRAN_TM = VALUES(SEC_TRAN_TM)
	,UPD_DTM = VALUES(UPD_DTM)

INSERT INTO BMS_DAY_DRV_ALLOC_INFO(
	OPER_DT
	,ALLOC_ID
	,ALLOC_NO
	,DRV_ID
	,SEC_DRV_ID
	,THR_DRV_ID
	,SEC_TRAN_TM
	,THR_TRAN_TM
)VALUES(
	DATE_FORMAT(#{OPER_DT}, '%Y-%m-%d')
	,#{ALLOC_ID}
	,#{ALLOC_NO}
	,#{DRV_ID}
	,#{SEC_DRV_ID}
	,#{THR_DRV_ID}
	,#{SEC_TRAN_TM}
	,#{THR_TRAN_TM}
)ON DUPLICATE KEY UPDATE
	DRV_ID = VALUES(DRV_ID)
	,SEC_DRV_ID = VALUES(SEC_DRV_ID)
	,THR_DRV_ID = VALUES(THR_DRV_ID)
	,SEC_TRAN_TM = VALUES(SEC_TRAN_TM)
	,THR_TRAN_TM = VALUES(THR_TRAN_TM)

SELECT IFNULL(MAX(SN)+1,1) FROM BMS_ALLOC_PL_INFO WHERE ALLOC_ID = #{ALLOC_ID}
INSERT INTO BMS_ALLOC_PL_INFO( ALLOC_ID, ALLOC_NO, SN, VHC_ID, DRV_ID, ST_ROUT_ID, ST_NODE_ID, ST_OPER_SN, ED_ROUT_ID, ED_NODE_ID, ED_OPER_SN, UPD_DTM, UPD_ID ) VALUES ( #{ALLOC_ID}, #{ALLOC_NO}, #{SN}, #{VHC_ID}, #{DRV_ID}, #{ST_ROUT_ID}, #{ST_NODE_ID}, #{ST_OPER_SN}, #{ED_ROUT_ID}, #{ED_NODE_ID}, #{ED_OPER_SN}, #{UPD_DTM}, #{SSN_USER_ID}
)

mapper.update:
UPDATE
	BMS_DAY_ALLOC_PL_INFO
SET
	VHC_ID = #{VHC_ID}
	,SEC_VHC_ID = #{SEC_VHC_ID}
	,SEC_TRAN_TM = #{SEC_TRAN_TM}
	,UPD_DTM = #{UPD_DTM}
WHERE
	ALLOC_ID = #{ALLOC_ID}
AND
	ALLOC_NO = #{ALLOC_NO}
AND
	OPER_DT = #{OPER_DT}

UPDATE
	BMS_DAY_DRV_ALLOC_INFO
SET
	DRV_ID = #{DRV_ID}
	,SEC_DRV_ID = #{SEC_DRV_ID}
	,THR_DRV_ID = #{THR_DRV_ID}
	,SEC_TRAN_TM = #{SEC_TRAN_TM}
	,THR_TRAN_TM = #{THR_TRAN_TM}
WHERE
	ALLOC_ID = #{ALLOC_ID}
AND
	ALLOC_NO = #{ALLOC_NO}
AND
	DAY_DIV = #{DAY_DIV}
AND
	OPER_DT = #{OPER_DT}

mapper.select:
SELECT A.ALLOC_ID , A.ALLOC_NO , A.SN AS ST_OPER_SN , A.ROUT_ID AS ST_ROUT_ID , B.ROUT_NM AS ST_ROUT_NM , A.ROUT_ST_TM , A.ROUT_ED_TM , B.ROUT_GRP , RG.DL_CD_NM AS ROUT_GRP_NM , B.WAY_DIV , WD.DL_CD_NM AS WAY_DIV_NM
FROM BMS_OPER_ALLOC_PL_ROUT_INFO A LEFT JOIN BMS_ROUT_MST B ON A.ROUT_ID = B.ROUT_ID LEFT JOIN BMS_DL_CD_INFO WD ON B.WAY_DIV = WD.DL_CD AND WD.CO_CD = 'WAY_DIV' LEFT JOIN BMS_DL_CD_INFO RG ON B.ROUT_GRP = RG.DL_CD AND RG.CO_CD = 'ROUT_GRP'
WHERE A.ALLOC_ID = #{ALLOC_ID}
AND A.ALLOC_NO = #{ALLOC_NO}
AND B.ROUT_NM LIKE CONCAT('%',#{CONTENT},'%') || A.ROUT_ID LIKE CONCAT('%',#{CONTENT},'%')
