mapper.insert:
INSERT INTO BMS_ALLOC_PL_INFO(
	ALLOC_ID
	,DAY_DIV
	,ALLOC_NO
	,WAY_DIV
	,VHC_ID
	,SEC_VHC_ID
	,SEC_TRAN_TM
	,UPD_DTM
	,UPD_ID
)VALUES(
	#{ALLOC_ID}
	,#{DAY_DIV}
	,#{ALLOC_NO}
	,#{WAY_DIV}
	,#{VHC_ID}
	,#{SEC_VHC_ID}
	,#{SEC_TRAN_TM}
	,#{UPD_DTM}
	,#{SSN_USER_ID}
)ON DUPLICATE KEY UPDATE
	VHC_ID = VALUES(VHC_ID)
	,SEC_VHC_ID = VALUES(SEC_VHC_ID)
	,SEC_TRAN_TM = VALUES(SEC_TRAN_TM)
	,UPD_DTM = VALUES(UPD_DTM)
	,UPD_ID	 = VALUES(UPD_ID)

INSERT INTO BMS_DAY_ALLOC_PL_INFO(
	OPER_DT
	,ALLOC_ID
	,ALLOC_NO
	,WAY_DIV
	,VHC_ID
	,SEC_VHC_ID
	,SEC_TRAN_TM
	,UPD_DTM
)VALUES(
	DATE_FORMAT(#{OPER_DT}, '%Y-%m-%d')
	,#{ALLOC_ID}
	,#{ALLOC_NO}
	,#{WAY_DIV}
	,#{VHC_ID}
	,#{SEC_VHC_ID}
	,#{SEC_TRAN_TM}
	,#{UPD_DTM}
)ON DUPLICATE KEY UPDATE
	VHC_ID = VALUES(VHC_ID)
	,SEC_VHC_ID = VALUES(SEC_VHC_ID)
	,SEC_TRAN_TM = VALUES(SEC_TRAN_TM)
	,UPD_DTM = VALUES(UPD_DTM)

INSERT INTO BMS_DAY_DRV_ALLOC_INFO(
	OPER_DT
	,ALLOC_ID
	,ALLOC_NO
	,DRV_ID
	,SEC_DRV_ID
	,THR_DRV_ID
	,SEC_TRAN_TM
	,THR_TRAN_TM
)VALUES(
	DATE_FORMAT(#{OPER_DT}, '%Y-%m-%d')
	,#{ALLOC_ID}
	,#{ALLOC_NO}
	,#{DRV_ID}
	,#{SEC_DRV_ID}
	,#{THR_DRV_ID}
	,#{SEC_TRAN_TM}
	,#{THR_TRAN_TM}
)ON DUPLICATE KEY UPDATE
	DRV_ID = VALUES(DRV_ID)
	,SEC_DRV_ID = VALUES(SEC_DRV_ID)
	,THR_DRV_ID = VALUES(THR_DRV_ID)
	,SEC_TRAN_TM = VALUES(SEC_TRAN_TM)
	,THR_TRAN_TM = VALUES(THR_TRAN_TM)

mapper.update:
UPDATE
	BMS_DAY_ALLOC_PL_INFO
SET
	VHC_ID = #{VHC_ID}
	,SEC_VHC_ID = #{SEC_VHC_ID}
	,SEC_TRAN_TM = #{SEC_TRAN_TM}
	,UPD_DTM = #{UPD_DTM}
WHERE
	ALLOC_ID = #{ALLOC_ID}
AND
	ALLOC_NO = #{ALLOC_NO}
AND
	OPER_DT = #{OPER_DT}

UPDATE
	BMS_DAY_DRV_ALLOC_INFO
SET
	DRV_ID = #{DRV_ID}
	,SEC_DRV_ID = #{SEC_DRV_ID}
	,THR_DRV_ID = #{THR_DRV_ID}
	,SEC_TRAN_TM = #{SEC_TRAN_TM}
	,THR_TRAN_TM = #{THR_TRAN_TM}
WHERE
	ALLOC_ID = #{ALLOC_ID}
AND
	ALLOC_NO = #{ALLOC_NO}
AND
	DAY_DIV = #{DAY_DIV}
AND
	OPER_DT = #{OPER_DT}
