mapper.update:
UPDATE 
	BMS_RPC_VHC_INFO
SET 
	INCDNT_ID = #{INCDNT_ID}
	,ORG_VHC_NO = #{ORG_VHC_NO}
	,RPC_VHC_NO = #{RPC_VHC_NO}
	,RPC_DRV_NM = #{RPC_DRV_NM}
	,RPC_DRV_ID = #{RPC_DRV_ID}

	,RPC_DTM = #{RPC_DTM}


	,GPS_X = #{GPS_X}


	,GPS_Y = #{GPS_Y}

	,DETAILS = #{DETAILS}
	,ORG_VHC_ID = #{ORG_VHC_ID}
	,RPC_VHC_ID = #{RPC_VHC_ID}
	,ROUT_ID = #{ROUT_ID}
	,ALLOC_NO = #{ALLOC_NO}
WHERE 
	RPC_ID = #{RPC_ID}

UPDATE 
	BMS_CUR_ALLOC_PL_VHC_INFO
SET	
	OPER_VHC_ID = #{RPC_VHC_ID}
WHERE 
	ROUT_GRP = #{ROUT_GRP} 
	AND ALLOC_NO = #{ALLOC_NO} 
	AND VHC_ID = #{GRP_VHC_ID}
