mapper.update:
UPDATE
	BMS_DAY_ALLOC_PL_INFO
SET
	VHC_ID = #{VHC_ID}
	,SEC_VHC_ID = #{SEC_VHC_ID}
	,SEC_TRAN_TM = #{SEC_TRAN_TM}
	,UPD_DTM = #{UPD_DTM}
WHERE
	ALLOC_ID = #{ALLOC_ID}
AND
	ALLOC_NO = #{ALLOC_NO}
AND
	OPER_DT = #{OPER_DT}

UPDATE
	BMS_DAY_DRV_ALLOC_INFO
SET
	DRV_ID = #{DRV_ID}
	,SEC_DRV_ID = #{SEC_DRV_ID}
	,THR_DRV_ID = #{THR_DRV_ID}
	,SEC_TRAN_TM = #{SEC_TRAN_TM}
	,THR_TRAN_TM = #{THR_TRAN_TM}
WHERE
	ALLOC_ID = #{ALLOC_ID}
AND
	ALLOC_NO = #{ALLOC_NO}

AND
	OPER_DT = #{OPER_DT}
