mapper.select:
SELECT			ROUT_ID, ROUT_NM
FROM			BMS_ROUT_MST
WHERE			DEL_YN = 'N'	
AND 			WAY_DIV = #{WAY_DIV}

	AND			ROUT_GRP = #{CONTENT1}

SELECT DATE_FORMAT(A.OPER_DT, '%Y-%m-%d') OPER_DT ,A.DAY_DIV ,A.WAY_DIV ,A.OPER_SN ,A.NODE_ID ,A.NODE_SN ,A.ALLOC_NO ,A.COR_ID ,A.NODE_TYPE ,A.ARRV_TM ,A.DPRT_TM
,DATE_FORMAT(A.UPD_DTM, '%Y-%m-%d') UPD_DTM ,B.ROUT_GRP_NM ,B.WAY_ASC_NM ,B.WAY_DESC_NM ,C.ROUT_ID ,C.NODE_NM ,C.ACCRU_LEN FROM BMS_DAY_OPER_ALLOC_PL_NODE_INFO A INNER JOIN BMS_ROUT_NODE_CMPSTN_VW C ON A.ROUT_ID = C.ROUT_ID AND A.NODE_ID = C.NODE_ID AND A.NODE_SN = C.NODE_SN
AND C.NODE_TYPE = #{NODE_TYPE}
WHERE A.OPER_DT = #{OPER_DT} AND A.DAY_DIV = #{DAY_DIV} AND C.ROUT_ID = #{ROUT_ID} AND A.NODE_TYPE = 'NT002'
AND A.OPER_SN = #{OPER_SN}
ORDER BY OPER_SN, NODE_SN

SELECT COUNT(*) CNT FROM BMS_OPER_ALLOC_PL_NODE_INFO A WHERE A.DAY_DIV = #{DAY_DIV} AND A.ROUT_GRP = #{ROUT_GRP} AND A.WAY_DIV = #{WAY_DIV}
AND A.ROUT_ID = #{ROUT_ID}
AND (A.NODE_TYPE = 'NT001' OR A.NODE_TYPE = 'NT002') GROUP BY OPER_SN ORDER BY OPER_SN
