mapper.select:
SELECT A.SLT_ID, A.DVC_ID, A.DVC_KIND, DL1.DL_CD_NM AS DVC_KIND_CD, A.PARAM_DIV, DL2.DL_CD_NM AS PARAM_DIV_CD, A.PARAM_KIND, DL3.DL_CD_NM AS PARAM_KIND_CD, A.SN, A.ST_TIME, A.ED_TIME, A.DATA_VAL, A.REMARK, DATE_FORMAT(A.UPD_DTM, '%Y-%m-%d') UPD_DTM, A.UPD_ID
FROM SLT_DVC_SCH_INFO A LEFT JOIN PLF_DL_CD_INFO DL1 ON A.DVC_KIND = DL1.DL_CD AND DL1.CO_CD = 'DVC_KIND' LEFT JOIN PLF_DL_CD_INFO DL2 ON A.PARAM_DIV = DL2.DL_CD AND DL2.CO_CD = 'PARAM_DIV' LEFT JOIN PLF_DL_CD_INFO DL3 ON A.PARAM_KIND = DL3.DL_CD AND DL3.CO_CD = 'PARAM_KIND'
WHERE SLT_ID = #{CONDITION} AND DVC_ID = #{DEVICE}

mapper.insert:
INSERT INTO	SLT_DVC_SCH_INFO
		(
			DVC_KIND
		,	PARAM_DIV
		,	PARAM_KIND
		,	DATA_TYPE
		,	DATA_LENGTH
		,	DATA_VAL
		,	ATTR1_VAL
		,	ATTR2_VAL
		,	SCHEDULE_YN
		,	USE_YN
		,	AUTO_YN
		,	REMARK
		,	UPD_DTM
		,	UPD_ID				
		)

VALUES (
			#{DVC_KIND}
		,	#{PARAM_DIV}
		,	#{PARAM_KIND}
		,	#{DATA_TYPE}
		,	#{DATA_LENGTH}
		,	#{DATA_VAL}
		,	#{ATTR1_VAL}
		,	#{ATTR2_VAL}
		,	#{SCHEDULE_YN}
		,	#{USE_YN}
		,	#{AUTO_YN}
		,	#{REMARK}
		,	#{REMARK}
		,	#{UPD_DTM}
		,	#{SSN_USER_ID}			
		)

mapper.update:
UPDATE  	SLT_DVC_SCH_INFO
SET		
			DVC_KIND = #{DVC_KIND}
		,	PARAM_DIV = #{PARAM_DIV}
		,	PARAM_KIND = #{PARAM_KIND}
		,	DATA_TYPE = #{DATA_TYPE}
		,	DATA_LENGTH = #{DATA_LENGTH}
		,	DATA_VAL = #{DATA_VAL}
		,	ATTR1_VAL = #{ATTR1_VAL}
		,	ATTR2_VAL = #{ATTR2_VAL}
		,	SCHEDULE_YN = #{SCHEDULE_YN}
		,	USE_YN = #{USE_YN}
		,	REMARK = #{REMARK}
		,	UPD_DTM = #{UPD_DTM}
		,	UPD_ID = #{UPD_ID}

WHERE		DVC_KIND = #{DVC_KIND}
AND			PARAM_DIV = #{PARAM_DIV}
AND			PARAM_KIND = #{PARAM_KIND}

mapper.delete:
DELETE
FROM		SLT_DVC_SCH_INFO
WHERE		DVC_KIND = #{DVC_KIND}
AND			PARAM_DIV = #{PARAM_DIV}
AND			PARAM_KIND = #{PARAM_KIND}
