mapper.select:
SELECT P.PROG_CD, P.PROG_NM, P.PROG_GRP_CD, P.PROG_PH , P.USE_YN, P.REMARK FROM PLF_PROG_MST P
AND P.USE_YN = #{CONTENT}
ORDER BY P.PROG_CD

mapper.insert:
INSERT INTO	PLF_PROG_MST
		(
			PROG_CD
		,	PROG_NM
		,	PROG_PH
		,	USE_YN
		,	REMARK
		,	UPD_DTM
		,	UPD_ID
		)

VALUES (
			#{PROG_CD}
		,	#{PROG_NM}
		,	#{PROG_PH}
		,	#{USE_YN}
		,	#{REMARK}
		,	#{UPD_DTM}
		,	#{SSN_USER_ID}			
		)

mapper.update:
UPDATE  	PLF_PROG_MST
SET		
			PROG_NM = #{PROG_NM} 
		,	PROG_PH = #{PROG_PH}
		,	USE_YN = #{USE_YN}
		,	REMARK = #{REMARK}
		,	UPD_DTM = #{UPD_DTM}
		,	UPD_ID = #{SSN_USER_ID}

WHERE		PROG_CD = #{PROG_CD}
