mapper.insert:
INSERT INTO	PLF_CO_CD_INFO
		(
			CO_CD
		,	CO_CD_NM
		,	SORT
		,	CO_CD_ENM
		,	USE_YN
		,	REMARK
		,	UPD_DTM
		,	UPD_ID
		)

VALUES (
			#{CO_CD}
		,	#{CO_CD_NM}
		,	#{SORT}
		,	#{CO_CD_ENM}
		,	#{USE_YN}
		,	#{REMARK}
		,	#{UPD_DTM}
		,	#{SSN_USER_ID}				
		)

mapper.update:
UPDATE  	PLF_CO_CD_INFO
SET		
			CO_CD_NM = #{CO_CD_NM}
		,	SORT = #{SORT}
		,	CO_CD_ENM = #{CO_CD_ENM}
		,	USE_YN = #{USE_YN}
		,	REMARK = #{REMARK}
		,	UPD_DTM = #{UPD_DTM}
		,	UPD_ID = #{UPD_ID}

WHERE		CO_CD = #{CO_CD}
