		,	FN4_AH
		,	FN5_AH
		,	FN6_AH
		,	FN7_AH
		,	FN8_AH
		,	HELP_AH
		,	UPD_DTM
		,	UPD_ID				
		)

VALUES (
			#{AUTH_CD}
		,	#{PROG_CD}
		,	#{AUTH_CHECK}
		,	#{SCH_AH}
		,	#{SAV_AH}
		,	#{GEX_AH}
