mapper.update:
UPDATE  	PLF_PROG_AUTH_INFO
SET		
			AUTH_CHECK = #{AUTH_CHECK}
		,	SCH_AH = #{SCH_AH}
		,	SAV_AH = #{SAV_AH}
		,	GEX_AH = #{GEX_AH}
		,	IEX_AH = #{IEX_AH}
		,	EXL_AH = #{EXL_AH}
		,	DEL_AH = #{DEL_AH}
		,	FN1_AH = #{FN1_AH}
		,	FN2_AH = #{FN2_AH}
		,	FN3_AH = #{FN3_AH}
		,	FN4_AH = #{FN4_AH}
		,	FN5_AH = #{FN5_AH}
		,	FN6_AH = #{FN6_AH}
