		,	FN8_AH = #{FN8_AH}
		,	HELP_AH = #{HELP_AH}
		,	UPD_DTM = #{UPD_DTM}
		,	UPD_ID	= #{SSN_USER_ID}

WHERE		AUTH_CD = #{AUTH_CD}
AND			PROG_CD = #{PROG_CD}

mapper.delete:
DELETE
FROM		PLF_PROG_AUTH_INFO
WHERE		AUTH_CD = #{AUTH_CD}
AND			PROG_CD = #{PROG_CD}
