		,	UPD_DTM
		,	UPD_ID
		)

VALUES (
			#{AUTH_CD} 

		,	#{AUTH_NM} 
		,	#{USE_YN}
		,	#{REMARK}
		,	#{UPD_DTM}
		,	#{SSN_USER_ID}				
