mapper.insert:
INSERT INTO	PLF_USER_MST
		(
			USER_ID
		,	USER_NM
		,	AUTH_CD
		,	USER_PS
		,	USE_YN
		,	REMARK
		,	UPD_DTM
		,	UPD_ID
		)

VALUES (
			#{USER_ID}
		,	#{USER_NM}
		,	#{AUTH_CD}
		,	#{USER_PS}
		,	#{USE_YN}
		,	#{REMARK}
		,	#{UPD_DTM}
		,	#{SSN_USER_ID}				
		)

mapper.update:
UPDATE  	PLF_USER_MST
SET		
			USER_NM = #{USER_NM}
		,	AUTH_CD = #{AUTH_CD}

		,	USER_PS = #{USER_PS}

		,	USE_YN = #{USE_YN}
		,	REMARK = #{REMARK}
		,	UPD_DTM = #{UPD_DTM}
		,	UPD_ID = #{SSN_USER_ID}

WHERE		USER_ID = #{USER_ID}
