mapper.select:
SELECT A.ROUT_GRP, B.ROUT_GRP_NM, A.DAY_DIV, A.WAY_DIV, A.ST_STTN_ID, (SELECT STTN_NM FROM BMS_STTN_MST WHERE STTN_ID = A.ST_STTN_ID AND DEL_YN!='Y') ST_STTN_NM, (SELECT STTN_ENM FROM BMS_STTN_MST WHERE STTN_ID = A.ST_STTN_ID AND DEL_YN!='Y') ST_STTN_ENM, A.ED_STTN_ID, (SELECT STTN_NM FROM BMS_STTN_MST WHERE STTN_ID = A.ED_STTN_ID AND DEL_YN!='Y') ED_STTN_NM, (SELECT STTN_ENM FROM BMS_STTN_MST WHERE STTN_ID = A.ED_STTN_ID AND DEL_YN!='Y') ED_STTN_ENM, A.OPER_CNT, A.ALLOC_CNT, A.FST_TM, A.LST_TM, A.AM_PEAK_ST_TM, A.AM_PEAK_ED_TM, A.PM_PEAK_ST_TM, A.PM_PEAK_ED_TM, A.AM_PEAK, A.PM_PEAK, A.NONE_PEAK, A.REMARK FROM BMS_OPER_PL_MST A LEFT OUTER JOIN BMS_ROUT_GRP_MST B ON A.ROUT_GRP = B.ROUT_GRP
B.USE_YN = 'Y' AND B.DEL_YN = 'N'
AND (A.ROUT_GRP LIKE CONCAT('%',#{CONTENT1},'%') || B.ROUT_GRP_NM LIKE CONCAT('%',#{CONTENT1},'%'))
AND A.DAY_DIV = #{CONTENT2}
AND A.WAY_DIV = #{CONTENT3}
ORDER BY A.ROUT_GRP, A.DAY_DIV, A.WAY_DIV

SELECT
DATE_FORMAT(A.OPER_DT, '%Y-%m-%d') OPER_DT ,A.ROUT_GRP ,A.ROUT_ID ,A.ALLOC_NO ,A.OPER_SN ,CONCAT(A.STAT_H, '시') AS STAT_H ,AVRG_SPD ,MAX_SPD ,MIN_SPD ,B.ROUT_NM ,B.ROUT_GRP_NM
DATE_FORMAT(DATE(A.OPER_DT), '%Y-%m-%d') OPER_DT ,AVG(A.AVRG_SPD) AS AVRG_SPD ,A.ROUT_ID ,A.ALLOC_NO ,A.OPER_SN ,A.ROUT_GRP ,B.ROUT_NM ,B.ROUT_GRP_NM
FROM BMS_ROUT_AVER_OPER_SP_STAT A LEFT JOIN BMS_ROUT_MST B ON A.ROUT_ID = B.ROUT_ID WHERE A.ROUT_GRP = #{ROUT_GRP}
AND DATE_FORMAT(A.OPER_DT, '%Y-%m-%d') BETWEEN #{F_DATE} AND #{L_DATE}
AND B.WAY_DIV = #{WAY_DIV}
GROUP BY A.OPER_SN ORDER BY A.OPER_SN
GROUP BY A.ALLOC_NO ORDER BY A.ALLOC_NO

SELECT
DATE_FORMAT(A.OPER_DT, '%Y-%m-%d') OPER_DT ,A.ROUT_GRP ,A.ROUT_ID ,A.ALLOC_NO ,A.OPER_SN ,CONCAT(A.STAT_H, '시') AS STAT_H ,AVRG_SPD ,MAX_SPD ,MIN_SPD ,B.ROUT_NM ,B.ROUT_GRP_NM
DATE_FORMAT(DATE(A.OPER_DT), '%Y-%m-%d') OPER_DT ,AVG(A.AVRG_SPD) AS AVRG_SPD ,A.ROUT_ID ,A.ALLOC_NO ,A.OPER_SN ,A.ROUT_GRP ,B.ROUT_NM ,B.ROUT_GRP_NM
FROM BMS_ROUT_AVER_OPER_SP_STAT A LEFT JOIN BMS_ROUT_MST B ON A.ROUT_ID = B.ROUT_ID WHERE A.ROUT_GRP = #{ROUT_GRP}
AND DATE_FORMAT(A.OPER_DT, '%Y-%m-%d') BETWEEN #{F_DATE} AND #{L_DATE}
AND B.WAY_DIV = #{WAY_DIV}
GROUP BY OPER_DT, A.OPER_SN ORDER BY OPER_DT, A.OPER_SN
GROUP BY OPER_DT, A.ALLOC_NO ORDER BY OPER_DT, A.ALLOC_NO
