mapper.select:
SELECT ATTACH_ID AS atchFileId, FILE_SN AS fileSn, ORG_FILE_NM AS orignlFileNm, FILE_NM AS streFileNm, FILE_SIZE AS fileMg, FILE_PATH AS fileStreCours, FILE_EXT AS fileExtsn FROM BMS_MULTI_ATTACH_DTL_INFO WHERE ATTACH_ID = #{atchFileId}
AND FILE_EXT = #{fileSn}
AND FILE_SN = #{fileSn}
